    /**
     * Run the database seeds.
     *
     * @return void
     */
    public function run()
    {
        foreach ($this->listing as $listing) {
            $attrs = array_only($listing, [
                'service_id', 'category_id',
                'name', 'type', 'unit'
            ]);

            $attrs['name'] = trim($attrs['name']);
            $field = new ListingField($attrs);

            $field->settings = array_only($listing, ['format', 'validation', 'default']);
            $field->save();

            if ($listing['options']) {
                if (str_contains($listing['options'], ['{', '}'])) {
                    $settings = $field->settings;
                    $field->settings = array_merge($settings, json_decode($listing['options'], true));
                    $field->save();
                }

                else {
                    $options = explode(',', $listing['options']);
                    $options_ar = explode(',', $listing['options_ar']);
                    foreach ($options as $index => $name) {
                        $option = $field->options()->create(['name' => trim($name)]);

                        if (isset($options_ar[$index])) {
                            $option->addTranslation([
                                'translatable_attribute' => 'name',
                                'translation' => trim($options_ar[$index])
                            ]);
                        }
                    }
                }
            }

            if (isset($listing['name_ar'])) {
                $field->addTranslation([
                    'translatable_attribute' => 'name',
                    'translation' => $listing['name_ar']
                ]);
            }

            if ($listing['unit'] && $listing['units_ar']) {
                $field->addTranslation([
                    'translatable_attribute' => 'unit',
                    'translation' => $listing['units_ar']
                ]);
            }
        }
    }
